$(function () {
  $(".menu-button").click(function () {
    $(".menu").slideToggle("active"), $(this).toggleClass("change");
  }),
    new Swiper(".swiper-container", {
      loop: !0,
      spaceBetween: 35,
      keyboard: {
        enabled: true,
        onlyInViewport: false,
      },
      breakpoints: {
        480: { slidesPerView: 1 },
        768: { slidesPerView: 2 },
        1271: { slidesPerView: 3 },
      },
      pagination: { el: ".swiper-pagination", clickable: !0 },
      navigation: {
        nextEl: ".swiper-button-next",
        prevEl: ".swiper-button-prev",
      },
    }),
    $("#menu").on("click", "a", function (a) {
      a.preventDefault();
      let t = $(this).attr("href"),
        n = $(t).offset().top;
      $("body,html").animate({ scrollTop: n }, 1000);
    }),
    $("#footer-menu").on("click", "a", function (a) {
      a.preventDefault();
      let t = $(this).attr("href"),
        n = $(t).offset().top;
      $("body,html").animate({ scrollTop: n }, 1000);
    }),
    $("body").on("touchstart", function () {}),
    new HystModal({ linkAttributeName: "data-hystmodal" }),
    $(window).width() <= 1024 &&
      $(".menu a").click(function (a) {
        $(".menu-button").click();
      });
  $('input[type="tel"]').inputmask({ mask: "+7 (999) 999-9999" });
});
